#include "compiler/codegen/peephole.h"

#include <stdbool.h>
#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#include "compiler/bytecode_utils.h"
#include "compiler/compiler.h"
#include "vm/vm.h"
